import { useState } from "react";
import { addUser, isNationalIdUnique } from "../utils/userService";
import { useNavigate } from "react-router-dom";

export default function Signup() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    nationalId: "",
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [profilePhoto, setProfilePhoto] = useState(null);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (!form.name || !form.email || !form.password || !form.nationalId) {
      return setError("تمام فیلدها الزامی هستند");
    }

    if (!isNationalIdUnique(form.nationalId)) {
      return setError("این کد ملی قبلاً ثبت شده است");
    }

    addUser(form);
    setSuccess("ثبت نام با موفقیت انجام شد!");

    setTimeout(() => {
      navigate("/login");
    }, 1500);
  }

  function handlePhotoUpload(e) {
  const file = e.target.files[0];
  if (file) {
    const previewURL = URL.createObjectURL(file);
    setProfilePhoto(previewURL);

    // ذخیره برای آینده (در دیتابیس)
    // فعلاً در localStorage ذخیره می‌کنیم:
    localStorage.setItem("signup_profile_photo", previewURL);
  }
}

  return (
  <div className="page-container">
    <h2>Signup</h2>

    {error && <p className="message-error">{error}</p>}
    {success && <p className="message-success">{success}</p>}

      <div className="profile-upload">
  <label className="profile-circle">
    <input
      type="file"
      accept="image/*"
      onChange={handlePhotoUpload}
      hidden
    />
    <img
      src={
        profilePhoto
          ? profilePhoto
          : "https://cdn-icons-png.flaticon.com/512/456/456212.png"
      }
      alt="profile"
    />
  </label>
  <p className="profile-text">آپلود عکس پروفایل</p>
</div>


    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="نام"
        value={form.name}
        onChange={handleChange}
      />

      <input
        type="email"
        name="email"
        placeholder="ایمیل"
        value={form.email}
        onChange={handleChange}
      />

      <input
        type="password"
        name="password"
        placeholder="پسورد"
        value={form.password}
        onChange={handleChange}
      />

      <input
        type="text"
        name="nationalId"
        placeholder="کد ملی"
        value={form.nationalId}
        onChange={handleChange}
      />

      <button type="submit">ثبت نام</button>
    </form>

    <p style={{ marginTop: "10px", textAlign:"center" }}>
      حساب داری؟{" "}
      <span
        className="link"
        onClick={() => navigate("/login")}
      >
        ورود
      </span>
    </p>
  </div>
);

}
